package session5;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 1;
		int num = 5;
		
		while(i<=10) {
			//System.out.println("i is: "+i);
			//System.out.println("num is: "+num);
			System.out.println(num+" "+i+"'s are "+num*i);
			i++;
		}
	}

}
